INSERT INTO `messages` (`id_mes`, `module_mes`, `language_mes`, `message_mes`) VALUES(1219, 'standard', 'fr', '<div class=\"rowComment\">\n	<h1>\n		Titre ou sous-titre (255 charactères max) :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"varchar\" id=\"<span class=\"keyword\">uniqueID</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n			<li>\n				Un attribut optionnel&nbsp;<span class=\"keyword\">default </span>permet de spécifier la valeur à afficher par défaut dans le tag en mode édition. Si cet attribut n\'est pas présent, un texte \"Lorem Ipsum\" sera affiché.<br />\n				&nbsp;</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Contenu textuel.</li>\n			<li>\n				<span class=\"vertclair\">{{jsdata}} :</span>&nbsp;Contenu textuel. Sans danger pour l\'utilisation Javascript (Quotes encodées)</li>\n		</ul>\n	</div>\n	<h1>\n		Texte mis en forme (HTML) :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"text\" id=\"<span class=\"keyword\">uniqueID</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n			<li>\n				Un attribut optionnel&nbsp;<span class=\"keyword\">default&nbsp;</span>permet de spécifier la valeur à afficher par défaut dans le tag en mode édition. Si cet attribut n\'est pas présent, un texte \"Lorem Ipsum\" sera affiché.</li>\n			<li>\n				Un attribut optionnel&nbsp;<span class=\"keyword\">styles&nbsp;</span>permet de spécifier le chemin vers un fichier CSS à employer dans le menu Styles de l\'éditeur.</li>\n			<li>\n				Un attribut optionnel&nbsp;<span class=\"keyword\">bgcolor&nbsp;</span>permet de spécifier la couleur de fond de l\'éditeur (au format hexadécimal).</li>\n			<li>\n				Un attribut optionnel&nbsp;<span class=\"keyword\">toolbar&nbsp;</span>permet de spécifier le nom de la barre d\'outil à employer lors de l\'édition de ce bloc.<br />\n				&nbsp;</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Contenu mis en forme (HTML).</li>\n		</ul>\n	</div>\n	<h1>\n		Image :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"image\" id=\"<span class=\"keyword\">uniqueID</span>\" maxWidth=\"<span class=\"keyword\">value</span>\" minWidth=\"<span class=\"keyword\">value</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n			<li>\n				<span class=\"keyword\">value :</span>&nbsp;Valeur minimum ou maximum autorisée pour l\'image en pixels. Les attributs maxWidth et minWidth sont optionnels.</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :<br />\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Code de l\'image et lien vers l\'image zoom si elle existe.</li>\n			<li>\n				<span class=\"vertclair\">{{label}}</span> ou <span class=\"vertclair\">{{linkLabel}} :</span>&nbsp;Nom / Légende de l\'image le cas échéant.</li>\n			<li>\n				<span class=\"vertclair\">{{jslabel}} :</span>&nbsp;Nom de l\'image (échappé pour insertion dans du javascript ou un attribut de tag).</li>\n			<li>\n				<span class=\"vertclair\">{{imageZoomHtml}} :</span>&nbsp;Code HTML affichant l\'image zoom si elle existe.</li>\n			<li>\n				<span class=\"vertclair\">{{imagePath}} :</span>&nbsp;Répertoire de l\'image sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageName}} :</span>&nbsp;Nom du fichier image sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageZoomHref}} :</span>&nbsp;Adresse (répertoire et nom) du fichier Image zoom sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageZoomName}} :</span>&nbsp;Nom du fichier image zoom sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageWidth}} :</span>&nbsp;Largeur du fichier image sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageHeight}} :</span>&nbsp;Hauteur du fichier image sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageZoomWidth}} :</span>&nbsp;Largeur du fichier image zoom sur le serveur.</li>\n			<li>\n				<span class=\"vertclair\">{{imageZoomHeight}} :</span>&nbsp;Hauteur du fichier image zoom sur le serveur.</li>\n		</ul>\n	</div>\n	<h1>\n		Fichier - Pièce jointe :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"file\" id=\"<span class=\"keyword\">uniqueID</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Lien vers le fichier si il existe.</li>\n			<li>\n				<span class=\"vertclair\">{{href}} :</span>&nbsp;Adresse (URL) du fichier.</li>\n			<li>\n				<span class=\"vertclair\">{{label}} :</span>&nbsp;Libellé du fichier.</li>\n			<li>\n				<span class=\"vertclair\">{{jslabel}} :</span>&nbsp;Libellé du fichier (échappé pour insertion dans du javascript ou un attribut de tag).</li>\n			<li>\n				<span class=\"vertclair\">{{size}} :</span>&nbsp;Taille du fichier en méga octets.</li>\n			<li>\n				<span class=\"vertclair\">{{filename}} :</span>&nbsp;Nom du fichier.</li>\n			<li>\n				<span class=\"vertclair\">{{originalfilename}} :</span>&nbsp;Nom original du fichier.</li>\n			<li>\n				<span class=\"vertclair\">{{type}} :</span>&nbsp;Extension du fichier.</li>\n			<li>\n				<span class=\"vertclair\">{{icon}} :</span>&nbsp;Icône de ce type de fichier si elle existe.</li>\n		</ul>\n	</div>\n	<h1>\n		Animation Flash :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"flash\" id=\"<span class=\"keyword\">uniqueID</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :<br />\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Contenu de l\'animation flash.</li>\n		</ul>\n	</div>\n	<h1>\n		Lien interne ou externe :</h1>\n	<div class=\"retrait\">\n		<span class=\"code\">&lt;block module=\"standard\" type=\"link\" id=\"<span class=\"keyword\">uniqueID</span>\"&gt;<span class=\"vertclair\">{{data}}</span>&lt;/block&gt;</span>\n		<ul>\n			<li>\n				<span class=\"keyword\">uniqueID :</span>&nbsp;Identifiant unique du bloc dans la rangée.</li>\n		</ul>\n		Les valeurs suivantes seront remplacées dans le tag :\n		<ul>\n			<li>\n				<span class=\"vertclair\">{{data}} :</span>&nbsp;Lien si il existe.</li>\n			<li>\n				<span class=\"vertclair\">{{href}} :</span>&nbsp;Adresse (URL) du lien.</li>\n			<li>\n				<span class=\"vertclair\">{{label}} :</span>&nbsp;Libellé du lien.</li>\n			<li>\n				<span class=\"vertclair\">{{jslabel}} :</span>&nbsp;Libellé du lien (échappé pour insertion dans du javascript ou un attribut de tag).</li>\n			<li>\n				<span class=\"vertclair\">{{target}}</span>&nbsp;: Cible du lien.</li>\n			<li>\n				<span class=\"vertclair\">{{type}}</span>&nbsp;: Type du lien&nbsp;(interne&nbsp;: 1, externe&nbsp;: 2, fichier&nbsp;: 3).</li>\n		</ul>\n	</div>\n</div>\n<p>\n	&nbsp;</p>\n');
